sys_script_include.script:
var MIDUtils = Class.create();
MIDUtils.prototype = {
    initialize: function() {},

    sync: function() {
        var command = "ls";

        var midServer = "dev";

        var gr = new GlideRecord('ecc_queue');
        gr.initialize();
        gr.name = command;
        gr.topic = "Command";
        gr.agent = "mid.server." + midServer;
        gr.source = "MIDUtils";
        gr.queue = "output";
        var eccId = gr.insert();

        var eccResponse = new GlideRecord('ecc_queue');
        eccResponse.addQuery("response_to", eccId);
        eccResponse.addQuery("queue", "input");
        eccResponse.query();

        while (!eccResponse.next()) {
            eccResponse.query();
            gs.sleep(250);
        }

        var xmlString = eccResponse.payload;

        if (xmlString == '<see_attachment/>') {
            // We need to get the xml string from the attachment...
            var gr = new GlideRecord('sys_attachment');
            gr.addQuery('table_name', 'ecc_queue');
            gr.addQuery('table_sys_id', eccResponse.sys_id);
            gr.query();

            while (gr.next()) {
                var ga = new GlideSysAttachment();
                xmlString = ga.getContent(gr); // getContent method only works in scoped applications
                //xmlBytes = ga.getBytes(gr);
                //xmlString = String(Packages.java.lang.String(xmlBytes)); 
            }
        }

        // Parse payload
        var xmlDoc = new XMLDocument2();
        xmlDoc.parseXML(xmlString);

        return xmlDoc.getNodeText('/results/result/stdout');
    },

    type: 'MIDUtils'
};
